          <Text style={styles.objective}>‚Ä¢ Resolver problemas reais</Text>
        </View>
      </View>

      {/* A√ß√µes da aula */}
      <View style={styles.lessonActions}>
        <TouchableOpacity style={styles.actionButton} onPress={handleTakeNotes}>
